rust.template:
// Grep node: {{nodeId}}
let {{nodeVar}}_pattern = {{rustString pattern}};
let {{nodeVar}}_case_insensitive = {{case_insensitive}};
let {{nodeVar}}_whole_words = {{whole_words}};
let {{nodeVar}}_invert_match = {{invert_match}};
let {{nodeVar}}_line_numbers = {{line_numbers}};
let {{nodeVar}}_count_only = {{count_only}};
let {{nodeVar}}_max_count = {{rustOptional max_count usize}};
let {{nodeVar}}_context_before = {{context_before}}usize;
let {{nodeVar}}_context_after = {{context_after}}usize;
let {{nodeVar}}_input_source = {{rustString input_source}};
let {{nodeVar}}_file_path = {{rustString file_path}};
let {{nodeVar}}_recursive = {{recursive}};
let {{nodeVar}}_file_pattern = {{rustString file_pattern}};

let {{nodeVar}}_result = execute_grep_operations(&{{nodeVar}}_pattern, {{inputVar}});
let {{nodeVar}}_matches = {{nodeVar}}_result.matches;
let {{nodeVar}}_match_count = {{nodeVar}}_result.match_count;
let {{nodeVar}}_success = {{nodeVar}}_result.success;
